"use strict";

document.addEventListener("DOMContentLoaded", function () {
  document.querySelector("header-top__logo, a").innerHTML = document.querySelector("header-top__logo, a").textContent.replace(/центр/, '<span style="color: red;">центр</span>');
  var mobMenuButton = document.querySelector(".header-top_mod-button-menu");

  if (mobMenuButton) {
    mobMenuButton.onclick = function () {
      switch (window.innerWidth < 769) {
        case document.querySelector(".nav-top").matches(".show"):
          document.querySelector(".header-top_mod-button-menu").classList.remove("mob");
          document.querySelector(".nav-top").classList.remove("show");
          break;

        default:
          document.querySelector(".header-top_mod-button-menu").classList.add("mob");
          document.querySelector(".nav-top").classList.add("show");
      }
    };
  }

  document.querySelectorAll('.menu-top__link').forEach(function (elem) {
    if (elem.href === window.location.href) {
      document.querySelectorAll('.menu-top__link').forEach(function (el) {
        el.classList.remove('active');
      });
      elem.classList.add('active');
    }
    /*elem.onclick = (e) => {
       // e.preventDefault();
        if( elem.href === window.location.href  ) {
            document.querySelectorAll('.menu-top__link').forEach(el => {
                el.classList.remove('active');
            });
            elem.classList.add('active');
        }
      }*/

  });
});